mapper.select:
SELECT IF( MAX(device_type_idx) IS NULL, 1,  MAX(device_type_idx)  + 1  ) AS VALUE
FROM device
WHERE gsm_key = #{gsmKey}
and device_type_id = #{deviceTypeId}

mapper.insert:
	INSERT INTO device (
		gsm_key, device_type_id,device_type_idx, nickname, device_type_nickname, green_house_id, controller_id,  mode, modbus_address1, modbus_address2, modbus_address3
		, id
	)
  	VALUES(
  		#{gsmKey}, #{deviceTypeId}, #{deviceTypeIdx} ,#{nickname}, #{deviceTypeNickname}, 0, #{controllerId}, 1, #{modbusAddress1}, #{modbusAddress2}, #{modbusAddress3}

		,NEXTVAL(gsm_seq_${gsmKey})


		,#{id}

	)

   		SELECT LASTVAL(gsm_seq_${gsmKey}) AS id;

mapper.update:
UPDATE device
SET
	 device_type_id = #{deviceTypeId} 
	,device_type_idx = #{deviceTypeIdx} 
	,controller_id = #{controllerId} 
	,nickname = #{nickname} 
	,device_type_nickname = #{deviceTypeNickname} 			
	,x_position = #{xPosition} 
	,y_position = #{yPosition} 
	,x2_position = #{x2Position} 
	,y2_position = #{y2Position} 
	,memo = #{memo} 
	,modbus_address1 = #{modbusAddress1} 
	,modbus_address2 = #{modbusAddress2} 
	,modbus_address3 = #{modbusAddress3} 
	,no_stop_control = #{noStopControl} 
	,MODE = #{mode} 
	,update_date = NOW() 
	,auto_manual_mode = #{autoManualMode} 
	,STATUS = #{status} 			
WHERE id = #{id};

mapper.delete:
DELETE
FROM device

WHERE gsm_key = #{gsm_key} AND controller_id = #{controller_id};

mapper.select:
SELECT id, NAME, description, last_update, device_type, device_type_name, device_type_group, kind, is_physical_device, manufacturer, model_name, model_spec, model_version, is_master, use_house_type, default_address1, default_address2, default_address3, dcac, min_value, MAX_VALUE, description_i18n, device_type_name_i18n,unit, round_number,topic_group,reset_point,accum_m,accum_h,accum_d,op_time,value_type FROM cd_device_type
AND kind = #{kind} AND use_house_type IN
#{item}

mapper.insert:
INSERT INTO device_v_dep_device(
	p_device_id, device_num, device_insert_order, device_id
)
VALUES(
	#{parentDeviceId}, #{deviceNum}, #{deviceInsertOrder}, #{deviceId}
);

	SELECT LAST_INSERT_ID() AS value;
