mapper.select:
SELECT IF( MAX(device_type_idx) IS NULL, 1,  MAX(device_type_idx)  + 1  ) AS VALUE
FROM device
WHERE gsm_key = #{gsmKey}
and device_type_id = #{deviceTypeId}

mapper.insert:
	INSERT INTO device (
		gsm_key, device_type_id,device_type_idx, nickname, device_type_nickname, green_house_id, controller_id,  mode, modbus_address1, modbus_address2, modbus_address3
		, properties
		, id
	)
  	VALUES(
  		#{gsmKey}, #{deviceTypeId}, #{deviceTypeIdx} ,#{nickname}, #{deviceTypeNickname}, 0, #{controllerId}, 1, #{modbusAddress1}, #{modbusAddress2}, #{modbusAddress3}
		,'${@com.kt.cmmn.util.MapUtils@toJson(properties)}'

		,NEXTVAL_device(#{gsmKey})


		,#{id}

	)

		SELECT #{id}LASTVAL_device(#{gsmKey}) AS id;

mapper.update:
UPDATE device
SET
	update_date = NOW()
	,device_type_id = #{deviceTypeId} 
	,device_type_idx = #{deviceTypeIdx} 
	,controller_id = #{controllerId} 
	,nickname = #{nickname} 
	,device_type_nickname = #{deviceTypeNickname} 			
	,x_position = #{xPosition} 
	,y_position = #{yPosition} 
	,x2_position = #{x2Position} 
	,y2_position = #{y2Position} 
	,memo = #{memo} 
	,modbus_address1 = #{modbusAddress1} 
	,modbus_address2 = #{modbusAddress2} 
	,modbus_address3 = #{modbusAddress3} 
	,MODE = #{mode} 
	,auto_manual_mode = #{autoManualMode} 
	,STATUS = #{status} 
	,properties = '${@com.kt.cmmn.util.MapUtils@toJson(properties)}' 
WHERE id = #{id};

mapper.delete:
DELETE
FROM device

WHERE gsm_key = #{gsm_key} AND controller_id = #{controller_id};

mapper.select:
SELECT 
	device.id, device_type_id, device_type_idx, user_info_id, gsm_key,
    device.green_house_id, controller_id, nickname, device_type_nickname,
	device.description, setup_date, x_position, y_position, x2_position,
	y2_position, memo, modbus_address1, modbus_address2, modbus_address3,
	`mode`, update_date, auto_manual_mode, `status`, properties
	,CDT.device_type, CDT.device_type_group, CDT.device_type_name, CDT.description AS device_type_description, CDT.kind, CDT.dcac
	,CDT.round_number,CDT.topic_group,CDT.reset_point,CDT.accum_m,CDT.accum_h,CDT.accum_d,CDT.op_time,CDT.value_type
	,CDT.opmode_error, CDT.value_exchange, CDT.sensing_timeout
FROM device
INNER JOIN cd_device_type CDT ON CDT.id = device.device_type_id

	INNER JOIN map_green_house_device mghd ON mghd.device_id = device.id


	AND gsm_key = #{gsm_key}
	AND controller_id = #{controller_id}
	AND device.id = #{device_id}
	AND device_type_id=#{device_type_id}
	AND mghd.green_house_id = #{house_id}
	AND mghd.green_house_id = #{green_house_id}

SELECT id, NAME, description, last_update, device_type, device_type_name, device_type_group, kind, is_physical_device, manufacturer, model_name, model_spec, model_version, is_master, use_house_type, default_address1, default_address2, default_address3, dcac, min_value, MAX_VALUE, description_i18n, device_type_name_i18n,unit, round_number,topic_group,reset_point,accum_m,accum_h,accum_d,op_time,value_type FROM cd_device_type
AND kind = #{kind} AND use_house_type IN
#{item}

mapper.insert:
INSERT INTO device_v_dep_device
(
	p_device_id, device_num, device_insert_order, device_id,gsm_key,id
)
VALUES(
	#{parentDeviceId}, #{deviceNum}, #{deviceInsertOrder}, #{deviceId},(SELECT gsm_key FROM device WHERE id = #{deviceId})
	,NEXTVAL_device_v_dep_device((SELECT gsm_key FROM device WHERE id = #{deviceId}))
	,#{id}
);

	SELECT #{id}LASTVAL_device_v_dep_device((SELECT gsm_key FROM device WHERE id = #{deviceId})) AS id;

mapper.delete:
DELETE 
FROM device_v_dep_device 

	AND id=#{id}
	AND p_device_id=#{pDeviceId}
	AND device_num=#{deviceNum}
	AND device_insert_order=#{deviceInsertOrder}

mapper.update:
UPDATE device_v_dep_device
	device_id = #{deviceId}

	id=#{id}
	AND p_device_id = #{parentDeviceId}
	AND device_num = #{deviceNum}
	AND device_insert_order = #{deviceInsertOrder}

mapper.insert:
INSERT INTO device_electric_dep_device(
p_device_id, device_id,gsm_key,id
)
VALUES(
#{parentDeviceId}, #{deviceId},(SELECT gsm_key FROM device WHERE id = #{deviceId})
,NEXTVAL_device_electric_dep_device((SELECT gsm_key FROM device WHERE id = #{deviceId}))
,#{id}
);


	SELECT #{id}LASTVAL_device_electric_dep_device((SELECT gsm_key FROM device WHERE id = #{deviceId})) AS id;

mapper.delete:
DELETE
FROM device_electric_dep_device
where id
in (
	SELECT id
	from device_electric_dep_device
	where p_device_id=#{parentDeviceId}
);
