mapper.select:
/*gsmOfDeviceList*/ SELECT gsm_info.gsm_key as gsmKey, gsm_info.user_info_id as userInfoId, is_mine as isMine, area_code as areaCode, farm_nickname as farmNickname, gsm_info.update_date as updateDate, owner_user_info_id as ownerUserInfoId, master_system_host as masterSystemHost, master_system_port as masterSystemPort, system_host as systemHost, system_port as systemPort, cropsurl, imsurl, mqtt_broker_host as mqttbrokerHost, mqtt_broker_port as mqttBrokerPort, server_read_time as serverReadTime, product_interval as productInterval, monitoring_interval as monitoringInterval, history_save_db_interval as historySaveDbInterval, sensor_current_retention_period as sensorCurrentRetentionPeriod, debug_stop_event as debugStopEvent, debug_stop_crops as debugStopCrops, debug_stop_device_auto_control as debugStopDeviceAutoContorl, debug_stop_ims as debugStopIms, delay_auto_control_start as delayAutoControlStart, package_version as packageVersion, schema_version as schemaVersion, nutrient_monitoring_interval as nutrientMonitoringInterval, farm_nickname_i18n as farmNicknameI18n, user_id as userId, farm_db_id as farmDbId, category_id as categoryId, COUNT(device.id) AS deviceCount, properties, UNIX_TIMESTAMP(NOW()) AS readTime FROM gsm_info LEFT OUTER JOIN device ON gsm_info.gsm_key = device.gsm_key
AND device.gsm_key = #{gsmKey}
GROUP BY gsm_info.gsm_key;

/*get*/ SELECT gsm_info.gsm_key as gsmKey, gsm_info.user_info_id as userInfoId, is_mine as isMine, area_code as areaCode, farm_nickname as farmNickname, gsm_info.update_date as updateDate, owner_user_info_id as ownerUserInfoId, master_system_host as masterSystemHost, master_system_port as masterSystemPort, system_host as systemHost, system_port as systemPort, cropsurl, imsurl, mqtt_broker_host as mqttbrokerHost, mqtt_broker_port as mqttBrokerPort, server_read_time as serverReadTime, product_interval as productInterval, monitoring_interval as monitoringInterval, history_save_db_interval as historySaveDbInterval, sensor_current_retention_period as sensorCurrentRetentionPeriod, debug_stop_event as debugStopEvent, debug_stop_crops as debugStopCrops, debug_stop_device_auto_control as debugStopDeviceAutoContorl, debug_stop_ims as debugStopIms, delay_auto_control_start as delayAutoControlStart, package_version as packageVersion, schema_version as schemaVersion, nutrient_monitoring_interval as nutrientMonitoringInterval, farm_nickname_i18n as farmNicknameI18n, user_id as userId, farm_db_id as farmDbId, category_id as categoryId, properties, UNIX_TIMESTAMP(NOW()) AS readTime FROM gsm_info
WHERE gsm_key = #{gsmKey}
LIMIT 1;

SELECT DISTINCT gi.gsm_key FROM gsm_category gc INNER JOIN ( SELECT DISTINCT REPLACE(SUBSTRING(gc.path,2, LENGTH(gc.path)-2),'][',',') id_path FROM ( SELECT gc.id, gc.name, gc.level, gc.pid, gc.priority, fn_category_path(gc.id) AS path FROM gsm_category gc ) gc INNER JOIN auth_group_detail ad ON gc.path LIKE CONCAT('%[',ad.category_id,']%') INNER JOIN user_info_auth_group ua ON ua.auth_group_id = ad.group_id WHERE ad.category_id != '0' AND ua.user_idx = #{authUserIdx} ) pt ON FIND_IN_SET(gc.id, pt.id_path) INNER JOIN gsm_info gi ON gi.category_id = gc.id
UNION ALL SELECT ad.gsm_key FROM auth_group_detail ad INNER JOIN user_info_auth_group ua ON ua.auth_group_id = ad.group_id WHERE ad.category_id = '0' AND ua.user_idx = #{authUserIdx}
UNION ALL SELECT gsm_key FROM gsm_info WHERE user_info_id = #{authUserIdx} OR (SELECT roles_id FROM user_info_roles WHERE roles_id = 100 AND user_idx = #{authUserIdx})

/*getGsmList*/ SELECT gsm_info.gsm_key as gsmKey, gsm_info.user_info_id as userInfoId, is_mine as isMine, area_code as areaCode, farm_nickname as farmNickname, gsm_info.update_date as updateDate, owner_user_info_id as ownerUserInfoId, master_system_host as masterSystemHost, master_system_port as masterSystemPort, system_host as systemHost, system_port as systemPort, mqtt_broker_host as mqttbrokerHost, mqtt_broker_port as mqttBrokerPort, server_read_time as serverReadTime, product_interval as productInterval, monitoring_interval as monitoringInterval, history_save_db_interval as historySaveDbInterval, delay_auto_control_start as delayAutoControlStart, package_version as packageVersion, schema_version as schemaVersion, nutrient_monitoring_interval as nutrientMonitoringInterval, farm_nickname_i18n as farmNicknameI18n, user_id as userId, farm_db_id as farmDbId, category_id as categoryId, properties, UNIX_TIMESTAMP(NOW()) AS readTime FROM gsm_info
AND gsm_info.gsm_key = #{gsmKey} AND gsm_info.user_info_id = #{userInfoId} AND gsm_info.category_id = #{categoryId} AND gsm_info.farm_nickname LIKE CONCAT('%', #{farmName}, '%')
AND gsm_info.gsm_key IN ( SELECT DISTINCT gi.gsm_key FROM gsm_category gc INNER JOIN ( SELECT DISTINCT REPLACE(SUBSTRING(gc.path,2, LENGTH(gc.path)-2),'][',',') id_path FROM ( SELECT gc.id, gc.name, gc.level, gc.pid, gc.priority, fn_category_path(gc.id) AS path FROM gsm_category gc ) gc INNER JOIN auth_group_detail ad ON gc.path LIKE CONCAT('%[',ad.category_id,']%') INNER JOIN user_info_auth_group ua ON ua.auth_group_id = ad.group_id WHERE ad.category_id != '0' AND ua.user_idx = #{authUserIdx} ) pt ON FIND_IN_SET(gc.id, pt.id_path) INNER JOIN gsm_info gi ON gi.category_id = gc.id
UNION ALL SELECT ad.gsm_key FROM auth_group_detail ad INNER JOIN user_info_auth_group ua ON ua.auth_group_id = ad.group_id WHERE ad.category_id = '0' AND ua.user_idx = #{authUserIdx}
UNION ALL SELECT gsm_key FROM gsm_info WHERE user_info_id = #{authUserIdx} OR (SELECT roles_id FROM user_info_roles WHERE roles_id = 100 AND user_idx = #{authUserIdx}) )

mapper.update:
