	);

SELECT LAST_INSERT_ID() AS id;

mapper.update:
UPDATE house_diary
title =#{title}, content =#{content}, diary_type_id =#{diaryTypeId}, diary_data ='${@com.kt.cmmn.util.MapUtils@toJson(diaryData)}', work =#{work}, etc =#{etc}, start_date =#{startDate}, end_date =#{endDate}, crops =#{crops}, harvest =#{harvest}, content_type=#{contentType}, work_time =#{workTime}, income =#{income}, spend =#{spend}
WHERE id=#{id}

mapper.select:
SELECT hd.id, hd.green_house_id, hdm.green_house_id AS house_id_list, hd.title, hd.content, hd.work, hd.etc, hd.start_date, hd.diary_data, hd.end_date, hd.crops, hd.harvest, hd.income, hd.spend, hd.content_type, hd.work_time, hd.cctv_image_url, hd.weather, dt.id diary_type_id, IFNULL(dt.base_diary_type_id, dt.id) base_diary_type_id, dt.is_base_diary FROM house_diary as hd INNER JOIN house_diary_map AS hdm on hdm.diary_id = hd.id INNER JOIN green_house AS gh ON gh.id = hdm.green_house_id INNER JOIN cd_diary_type AS dt ON dt.id = hd.diary_type_id
gh.gsm_key = #{gsm_key}
#{item}
AND gh.id = #{green_house_id} AND dt.id = #{diary_type_id} AND (dt.base_diary_type_id = #{base_diary_type_id} OR dt.id = #{base_diary_type_id}) AND dt.is_base_diary = #{is_base_diary}
AND ( start_date BETWEEN #{start_date} AND #{end_date} OR end_date BETWEEN #{start_date} AND #{end_date} )

