hd.diary_type_id = 4 AND DATE(hd.start_date) = CURRENT_DATE() AND JSON_SEARCH( diary_data, 'one', #{cctvImageUrl}) IS NOT NULL
#{hid}

mapper.update:
UPDATE house_diary hd INNER JOIN house_diary_map hdm ON hd.id = hdm.diary_id SET hd.diary_data = JSON_ARRAY_APPEND( hd.diary_data, '$.imageUrl', #{cctvImageUrl})
hd.diary_type_id = 4 AND DATE(hd.start_date) = CURRENT_DATE() AND JSON_SEARCH( hd.diary_data, 'one', #{cctvImageUrl}) IS NULL
