mapper.insert:
INSERT INTO house_diary
	(
		green_house_id,title,content,work,etc,start_date,end_date,crops,harvest,income,spend,content_type,work_time,cctv_image_url,weather
	)
VALUES 
	(
		#{greenHouseId},#{title},#{content},#{work},#{etc},#{startDate},#{endDate},#{crops},#{harvest},#{income},#{spend},#{contentType},#{workTime},#{cctvImageUrl},#{weather}
	);

SELECT LAST_INSERT_ID() AS id;

mapper.update:
UPDATE house_diary
title =#{title}, content =#{content}, work =#{work}, etc =#{etc}, start_date =#{startDate}, end_date =#{endDate}, crops =#{crops}, harvest =#{harvest}, content_type=#{contentType}, work_time =#{workTime}, income =#{income}, spend =#{spend}
WHERE id=#{id} AND green_house_id = #{greenHouseId}

mapper.select:
SELECT hd.id, hd.green_house_id, hdm.green_house_id AS house_id_list, hd.title, hd.content, hd.work, hd.etc, hd.start_date, hd.end_date, hd.crops, hd.harvest, hd.income, hd.spend, hd.content_type, hd.work_time, hd.cctv_image_url, hd.weather FROM house_diary as hd INNER JOIN house_diary_map AS hdm on hdm.diary_id = hd.id INNER JOIN green_house AS gh ON gh.id = hdm.green_house_id
gh.gsm_key = #{gsm_key}
#{item}
AND gh.id = #{green_house_id} AND start_date BETWEEN #{start_date} AND #{end_date} AND end_date BETWEEN #{start_date} AND #{end_date}

mapper.delete:
DELETE FROM  house_diary_file
WHERE house_diary_idx = #{id}

	AND  1 = 2	


