	)
VALUES(
	#{greenHouseId},#{leafWidth},#{stem},#{harvest},#{fruitSet},#{getherSet},#{fruitHeight},#{bloom},#{fruitNumber},#{averWeight},#{growLength},#{leafSize},#{leafNum},#{startDate},#{uploadFile},#{content},#{title},#{crops}, #{contentType}, #{cctvImageUrl},#{weather}
)

SELECT LAST_INSERT_ID() AS id;

mapper.update:
UPDATE crops_diary
leaf_width = #{leafWidth}, stem = #{stem}, harvest = #{harvest}, fruit_set = #{fruitSet}, gether_set = #{getherSet}, fruit_height = #{fruitHeight}, bloom = #{bloom}, fruit_number = #{fruitNumber}, aver_weight = #{averWeight}, grow_length = #{growLength}, leaf_size = #{leafSize}, leaf_num = #{leafNum}, start_date = #{startDate}, upload_file = #{uploadFile}, content = #{content}, title = #{title}, crops = #{crops}, content_type = #{contentType}
WHERE id = #{id} AND green_house_id = #{greenHouseId};

mapper.delete:
DELETE FROM  crops_diary_file
WHERE crops_diary_idx = #{id}

AND
 1 = 2	


		#{file_idx_list}

mapper.select:
SELECT cd.id, cd.green_house_id, cdm.green_house_id AS house_id_list, cd.leaf_width, cd.stem, cd.harvest, cd.fruit_set, cd.gether_set, cd.fruit_height, cd.bloom, cd.fruit_number, cd.aver_weight, cd.grow_length, cd.leaf_size, cd.leaf_num, cd.start_date, cd.upload_file, cd.content, cd.title, cd.crops, cd.content_type, cd.cctv_image_url, cd.weather FROM crops_diary as cd INNER JOIN crops_diary_map AS cdm on cdm.diary_id = cd.id INNER JOIN green_house AS gh ON gh.id = cdm.green_house_id
gh.gsm_key = #{gsm_key}
#{item}
AND cdm.green_house_id = #{green_house_id}
AND start_date BETWEEN #{start_date} AND #{end_date}
