mapper.insert:
		INSERT INTO green_house( 
       		product_method_id, green_house_type_id, house_name, latitude, longitude, description, build_date, last_update, addr_1, addr_2, zip, nutrient_common_id, change_gh, change_temperature, floor_space, house_horizontal, house_vertical, house_height, plant_spacing, crop_cnt, house_direction, select_temp_sensor, select_hum_sensor, gsm_key, house_name_i18n
		,id
		)
		VALUES (
			#{productMethodId},#{greenHouseTypeId},#{houseName},#{latitude},#{longitude},#{description},now(),now(),#{addr1},#{addr2},#{zip},1,1,1,#{floorSpace},#{houseHorizontal},#{houseVertical},#{houseHeight},#{plantSpacing},#{cropCnt},#{houseDirection},#{selectTempSensor},#{selectHumSensor},#{gsmKey},#{houseNameI18n}

			,#{id}


			,NEXTVAL(gsm_seq_${gsmKey})

		);

			SELECT LASTVAL(gsm_seq_${gsmKey}) AS id;

mapper.delete:
DELETE
FROM map_green_house_device
WHERE green_house_id = #{house_id} AND device_id = #{device_id};

mapper.update:
UPDATE green_house
SET
		product_method_id = #{productMethodId}
	, green_house_type_id = #{greenHouseTypeId}
	,	house_name = #{houseName}
	,	latitude = #{latitude}
	,	longitude = #{longitude}
	, description =#{description}
	,	addr_1 = #{addr1}
	,	addr_2 = #{addr2}
	,	zip = #{zip}
	, nutrient_common_id =#{nutrientCommonId} 
	,	change_gh = #{changeGh}
	,change_temperature =#{changeTemperature}
	,	floor_space = #{floorSpace}
	, house_horizontal =#{houseHorizontal}
	,	house_vertical = #{houseVertical}
	, house_height=#{houseHeight}
	,	plant_spacing = #{plantSpacing}
	,	crop_cnt = #{cropCnt}
	,	house_direction = #{houseDirection}
	,select_temp_sensor = #{selectTempSensor}
	,select_hum_sensor = #{selectHumSensor}	
	,	house_name_i18n = #{houseNameI18n}
	,last_update = NOW()	
WHERE gsm_key = #{gsmKey} AND id = #{id};

mapper.delete:
DELETE
FROM green_house
WHERE gsm_key = #{gsmKey} AND id = #{id};

mapper.select:
SELECT controller.id, controller.gsm_key as gsmKey, controller.controller_info_id as controllerInfoId, controller.controller_type as controllerType, controller.ip, controller.PORT, controller.controller_status as controllerStatus, controller.description FROM device INNER JOIN controller ON controller.id = device.controller_id WHERE device.gsm_key = #{gsm_key}
AND FALSE
#{item}
GROUP BY controller_id;

mapper.insert:
		INSERT INTO	cctv	
           ( house_id, cctv_name, cctv_uid, cctv_pwd, cctv_ws_url, cctv_rtsp_url, is_rtsp_play, wowza_id)
		VALUES(
			#{houseId},#{cctvName},#{cctvUid},#{cctvPwd},#{cctvWsUrl},#{cctvRtspUrl},
			IF(#{isRtspPlay} = 'true', 1, IF( #{isRtspPlay} = 'false' , 0, #{isRtspPlay})),
			#{wowzaId}
		)

			SELECT LAST_INSERT_ID() AS id;

mapper.update:
UPDATE cctv
SET
	 cctv_name = #{cctvName}
	, cctv_uid = #{cctvUid}
	, cctv_pwd = #{cctvPwd}
	, cctv_ws_url = #{cctvWsUrl}
	, cctv_rtsp_url = #{cctvRtspUrl}
	, is_rtsp_play = IF(#{isRtspPlay} = 'true', 1, IF( #{isRtspPlay} = 'false' , 0, #{isRtspPlay}))
	, wowza_id = #{wowzaId}
WHERE house_id = #{houseId} AND id = #{id};

mapper.insert:
INSERT INTO weather_cast (
	base_date, base_time, fcst_date, fcst_time, fcst_value, nx, ny, category, house_id
)
VALUES (
	#{base_date}, #{base_time}, #{fcst_date}, #{fcst_time}, #{fcst_value}, #{nx}, #{ny}, #{category}, #{house_id}
)

	SELECT LAST_INSERT_ID() AS id;

mapper.select:
SELECT house_id, base_date, base_time, fcst_date, fcst_time, fcst_value, nx, ny, category FROM weather_cast WHERE id IN ( SELECT MAX(id) FROM weather_cast WHERE 1=1 AND house_id = #{house_id}
AND fcst_date BETWEEN #{from_date} AND #{to_date}
AND fcst_date BETWEEN CURRENT_DATE AND CURRENT_DATE + INTERVAL 3 DAY
GROUP BY house_id, fcst_date, fcst_time, category ) ORDER BY id;

