mapper.delete:
DELETE
FROM green_house
WHERE gsm_key = #{gsmKey} AND id = #{id};

mapper.select:
SELECT controller.id, controller.gsm_key as gsmKey, controller.controller_info_id as controllerInfoId, controller.controller_type as controllerType, controller.ip, controller.PORT, controller.controller_status as controllerStatus, controller.description, controller.properties FROM device INNER JOIN controller ON controller.id = device.controller_id WHERE device.gsm_key = #{gsm_key}
AND FALSE
#{item}
GROUP BY controller_id;

mapper.update:
UPDATE cctv

	 cctv_name = #{cctvName}
	, cctv_uid = #{cctvUid}
	, cctv_pwd = #{cctvPwd}
	, cctv_ws_url = #{cctvWsUrl}
	, cctv_rtsp_url = #{cctvRtspUrl}
	, mini_vms_url = #{miniVmsUrl}
	, mini_vms_idx = #{miniVmsIdx}
	, is_rtsp_play = IFNULL(CASE #{isRtspPlay} WHEN 'true' THEN 1 WHEN 'false' THEN 0 ELSE #{isRtspPlay} END, 0)
	, is_mini_vms =  IFNULL(CASE #{isMiniVms} WHEN 'true' THEN 1 WHEN 'false' THEN 0 ELSE #{isMiniVms} END, 0)
	, enable_ptz = IFNULL(CASE #{enablePtz} WHEN 'true' THEN 1 WHEN 'false' THEN 0 ELSE #{enablePtz} END, 0)
	, wowza_id = #{wowzaId}
	, properties = '${@com.kt.cmmn.util.MapUtils@toJson(properties)}'

WHERE house_id = #{houseId} AND id = #{id};

UPDATE cctv c
	INNER JOIN green_house as gh on gh.id = c.house_id
SET  c.rtc_stream_hash = #{rtcStreamHash}
WHERE gh.gsm_key = #{gsmKey} AND c.mini_vms_idx = #{miniVmsIdx}

mapper.insert:
INSERT INTO weather_cast (
	base_date, base_time, fcst_date, fcst_time, fcst_value, nx, ny, category, house_id
)
VALUES (
	#{base_date}, #{base_time}, #{fcst_date}, #{fcst_time}, #{fcst_value}, #{nx}, #{ny}, #{category}, #{house_id}
)

	SELECT LAST_INSERT_ID() AS id;

mapper.select:
SELECT house_id, base_date, base_time, fcst_date, fcst_time, fcst_value, nx, ny, category FROM weather_cast WHERE id IN ( SELECT MAX(id) FROM weather_cast WHERE 1=1 AND house_id = #{house_id}
AND fcst_date BETWEEN #{from_date} AND #{to_date}
AND fcst_date BETWEEN CURRENT_DATE AND CURRENT_DATE + INTERVAL 3 DAY
GROUP BY house_id, fcst_date, fcst_time, category ) ORDER BY id;

mapper.insert:
INSERT INTO ultra_weather_cast(
