mapper.delete:
DELETE
FROM device
WHERE gsm_key = #{gsm_key} AND id = #{id};

mapper.insert:
INSERT INTO device_v_dep_device(
	p_device_id, device_num, device_insert_order, device_id
)
VALUES(
	#{parentDeviceId}, #{deviceNum}, #{deviceInsertOrder}, #{deviceId}
);

	SELECT LAST_INSERT_ID() AS value;
