mapper.update:

UPDATE gsm_info
SET 
	 gsm_key= #{gsmKey}
	 , user_info_id= #{userInfoId}
	 , is_mine= #{isMine}
	 , area_code= #{areaCode}
	 , farm_nickname= #{farmNickname}
	 , update_date= NOW()
	 , owner_user_info_id= #{ownerUserInfoId}
	 , master_system_host= #{masterSystemHost}
	 , master_system_port= #{masterSystemPort}
	 , system_host= #{systemHost}
	 , system_port= #{systemPort}
	 , vpn_system_host= #{vpnSystemHost}
	 , vpn_system_port= #{vpnSystemPort}
	 , cropsurl= #{cropsurl}
	 , imsurl= #{imsurl}
	 , mqtt_broker_host= #{mqttbrokerHost}
	 , mqtt_broker_port= #{mqttBrokerPort}
	 , server_read_time= #{serverReadTime}
	 , product_interval= #{productInterval}
	 , monitoring_interval= #{monitoringInterval}
	 , history_save_db_interval= #{historySaveDbInterval}
	 , sensor_current_retention_period= #{sensorCurrentRetentionPeriod}
	 , debug_stop_event= #{debugStopEvent}
	 , debug_stop_crops= #{debugStopCrops}
	 , debug_stop_device_auto_control= #{debugStopDeviceAutoContorl}
	 , debug_stop_ims= #{debugStopIms}
	 , delay_auto_control_start = #{delayAutoControlStart}
	 , package_version = #{packageVersion}
	 , schema_version = #{schemaVersion}
	 , nutrient_monitoring_interval= #{nutrientMonitoringInterval}
	 , farm_nickname_i18n = #{farmNicknameI18n}
	 , user_id = #{userId}
	 , farm_db_id = #{farmDbId}
	 , category_id = #{categoryId}
	 ,monitoring_process_interval = #{monitoringProcessInterval}
WHERE gsm_key = #{gsmKey}

mapper.select:
/*gsmOfDeviceList*/ SELECT gsm_info.gsm_key as gsmKey, gsm_info.user_info_id as userInfoId, is_mine as isMine, area_code as areaCode, farm_nickname as farmNickname, gsm_info.update_date as updateDate, owner_user_info_id as ownerUserInfoId, master_system_host as masterSystemHost, master_system_port as masterSystemPort, system_host as systemHost, system_port as systemPort, cropsurl, imsurl, mqtt_broker_host as mqttbrokerHost, mqtt_broker_port as mqttBrokerPort, server_read_time as serverReadTime, product_interval as productInterval, monitoring_interval as monitoringInterval, history_save_db_interval as historySaveDbInterval, sensor_current_retention_period as sensorCurrentRetentionPeriod, debug_stop_event as debugStopEvent, debug_stop_crops as debugStopCrops, debug_stop_device_auto_control as debugStopDeviceAutoContorl, debug_stop_ims as debugStopIms, delay_auto_control_start as delayAutoControlStart, package_version as packageVersion, schema_version as schemaVersion, nutrient_monitoring_interval as nutrientMonitoringInterval, farm_nickname_i18n as farmNicknameI18n, user_id as userId, farm_db_id as farmDbId, category_id as categoryId, COUNT(device.id) AS deviceCount FROM gsm_info LEFT OUTER JOIN device ON gsm_info.gsm_key = device.gsm_key
AND device.gsm_key = #{gsmKey}
GROUP BY gsm_info.gsm_key;

mapper.update:
UPDATE gsm_info
SET 
	user_info_id = #{userInfoId}
WHERE gsm_key = #{gsmKey}
