mapper.insert:
INSERT INTO house_diary
	(
		green_house_id,title,content,work,etc,start_date,end_date,crops,harvest,income,spend,content_type,work_time,cctv_image_url,weather
	)
VALUES 
	(
		#{greenHouseId},#{title},#{content},#{work},#{etc},#{startDate},#{endDate},#{crops},#{harvest},#{income},#{spend},#{contentType},#{workTime},#{cctvImageUrl},#{weather}
	);

SELECT LAST_INSERT_ID() AS id;

mapper.update:
UPDATE house_diary
title =#{title}, content =#{content}, work =#{work}, etc =#{etc}, start_date =#{startDate}, end_date =#{endDate}, crops =#{crops}, harvest =#{harvest}, income =#{income}, spend =#{spend}, content_type=#{contentType}, work_time =#{workTime}
WHERE id=#{id} AND green_house_id = #{greenHouseId}

UPDATE crops_diary
leaf_width = #{leafWidth}, stem = #{stem}, harvest = #{harvest}, fruit_set = #{fruitSet}, gether_set = #{getherSet}, fruit_height = #{fruitHeight}, bloom = #{bloom}, fruit_number = #{fruitNumber}, aver_weight = #{averWeight}, grow_length = #{growLength}, leaf_size = #{leafSize}, leaf_num = #{leafNum}, start_date = #{startDate}, upload_file = #{uploadFile}, content = #{content}, title = #{title}, crops = #{crops}, content_type = #{contentType}
WHERE id = #{id} AND green_house_id = #{greenHouseId};
